        #region METODOS
        public string Realizar_Backup(string ruta, string nombre)
        {
            try
            {
                SelectCommandText = "USE[Canchas_2022] BACKUP DATABASE Canchas_2022 TO DISK = '" + ruta + nombre + ".bak' WITH FORMAT, MEDIANAME = 'SQLServerBackups', NAME = 'Full Backup of Canchas'";

                ExecuteNonReader();
                return "La copia se ha creado correctamente en la ruta en" + ruta + ".";
            }
            catch
            {
                throw new Exception("Error al realizar la copia.");
            }
        }

        public string Realizar_Restore(string ruta)
        {
            try
            {
                SelectCommandText = $@"ALTER DATABASE[Canchas_2022] SET SINGLE_USER WITH ROLLBACK IMMEDIATE USE MASTER RESTORE DATABASE[Canchas_2022] FROM DISK = '{ruta}' WITH REPLACE ALTER DATABASE [Canchas_2022] SET MULTI_USER";
                ExecuteNonReader();

                return "Se restauró el sistema de manera correcta.";
            }
            catch
            {
                throw new Exception("Error al realizar el restore.");
            }
        }

        public bool CrearBaseDeDatos(string server, string nombreBase)
        {
            try
            {
                bool existeBD = true;
                existeBD = VerificarExistenciaBaseDeDatos(server, nombreBase);

                if (existeBD == false)
                {
                    string script = CREAR_BASE_DE_DATOS;
                    IEnumerable<string> query = FormatearQueryScript(script);
                    ExecuteNonQueryCreateDB(server, query);
                }
                return existeBD;
            }
            catch (Exception ex)
            {
                throw new Exception(ex.Message);
            }
        }
        #endregion

        private IEnumerable<string> FormatearQueryScript(string script)
        {
            // Reemplazo los carácteres por espacio
            script = Regex.Replace(script, @"(\r\n|\n\r|\n|\r)", "\n");

            // Elimino los "GO"
            string[] condiciones = Regex.Split(
                    script,
                    @"^[\t ]*GO[\t ]*\d*[\t ]*(?:--.*)?$",
                    RegexOptions.Multiline |
                    RegexOptions.IgnorePatternWhitespace |
                    RegexOptions.IgnoreCase);

            // Aplico las condiciones para generar una lista de string, separando el script
            return condiciones
                .Where(x => !string.IsNullOrWhiteSpace(x))
                .Select(x => x.Trim(' ', '\n'));
        }
    }
